var promisify = require('promisify-es6');

var streamToValueWithTransformer = require('../utils/stream-to-value-with-transformer');

var multiaddr = require('multiaddr');

var PeerId = require('peer-id');

var PeerInfo = require('peer-info');

var errcode = require('err-code');

module.exports = function (send) {
  return promisify(function (cid, opts, callback) {
    if (typeof opts === 'function' && !callback) {
      callback = opts;
      opts = {};
    } // opts is the real callback --
    // 'callback' is being injected by promisify


    if (typeof opts === 'function' && typeof callback === 'function') {
      callback = opts;
      opts = {};
    }

    var handleResult = function handleResult(res, callback) {
      // Inconsistent return values in the browser vs node
      if (Array.isArray(res)) {
        res = res[0];
      } // Type 4 keys


      if (res.Type !== 4) {
        var errMsg = "key was not found (type 4)";
        return callback(errcode(new Error(errMsg), 'ERR_KEY_TYPE_4_NOT_FOUND'));
      }

      var responses = res.Responses.map(function (r) {
        var peerInfo = new PeerInfo(PeerId.createFromB58String(r.ID));
        r.Addrs.forEach(function (addr) {
          var ma = multiaddr(addr);
          peerInfo.multiaddrs.add(ma);
        });
        return peerInfo;
      });
      callback(null, responses);
    };

    send({
      path: 'dht/findprovs',
      args: cid,
      qs: opts
