'use strict';

var streamifier = require('streamifier');

var promisify = require('promisify-es6');

var SendOneFile = require('../utils/send-one-file');

module.exports = function (send) {
  var sendOneFile = SendOneFile(send, 'config/replace');
  return promisify(function (config, callback) {
    if (typeof config === 'object') {
      config = streamifier.createReadStream(Buffer.from(JSON.stringify(config)));
    }

    sendOneFile(config, {}, callback);
  });
};
