'use strict';

var dagPB = require('ipld-dag-pb');

var dagCBOR = require('ipld-dag-cbor');

var promisify = require('promisify-es6');

var CID = require('cids');

var waterfall = require('async/waterfall');

var block = require('../block');

var resolvers = {
  'dag-cbor': dagCBOR.resolver,
  'dag-pb': dagPB.resolver
};

module.exports = function (send) {
  return promisify(function (cid, path, options, callback) {
    if (typeof path === 'function') {
      callback = path;
      path = undefined;
    }

    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    options = options || {};
    path = path || '';

    if (CID.isCID(cid)) {
      cid = cid.toBaseEncodedString();
    }

    waterfall([function (cb) {
      send({
        path: 'dag/resolve',
        args: cid + '/' + path,
        qs: options
      }, cb);
    }, function (resolved, cb) {
      block(send).get(new CID(resolved['Cid']['/']), function (err, ipfsBlock) {
        cb(err, ipfsBlock, resolved['RemPath']);
      });
    }, function (ipfsBlock, path, cb) {
      var dagResolver = resolvers[ipfsBlock.cid.codec];

      if (!dagResolver) {
        var error = new Error('ipfs-http-client is missing DAG resolver for "' + ipfsBlock.cid.codec + '" multicodec');
        error.missingMulticodec = ipfsBlock.cid.codec;
        cb(error);
        return;
      }

      dagResolver.resolve(ipfsBlock.data, path, cb);
    }], callback);
  });
};
